import React, { useState, useEffect, useContext } from 'react';
import {
  Container, Row, Col, Button, Card,
} from 'react-bootstrap';
import styled, { ThemeContext } from 'styled-components';
import PropTypes from 'prop-types';
import Fade from 'react-reveal/Fade';
import endpoints from '../constants/endpoints';
import FallbackSpinner from './FallbackSpinner';

// Styled components
const StyledContainer = styled(Container)`
  margin-bottom: 25px;
  padding-top: 25px;
  padding-bottom: 25px;
  background-color: ${({ theme }) => theme.colors.background};
  border-radius: 8px;
  box-shadow: ${({ theme }) => theme.shadows[4]};
`;

const StyledButton = styled(Button)`
  margin: 25px;
  background-color: ${({ theme }) => theme.colors.secondary};
  color: ${({ theme }) => theme.colors.secondaryContrast};
  &:hover {
    background-color: ${({ theme }) => theme.colors.secondaryDark};
  }
`;

const StyledCard = styled(Card)`
  border-radius: 15px;
  box-shadow: ${({ theme }) => theme.shadows[3]};
  transition: 0.3s;
  &:hover {
    box-shadow: ${({ theme }) => theme.shadows[8]};
  }
`;

const StyledCardImage = styled(Card.Img)`
  height: 180px;
`;

const Projects = ({ header }) => {
  const theme = useContext(ThemeContext);
  const [data, setData] = useState(null);
  const [showMore, setShowMore] = useState(false);

  useEffect(() => {
    fetch(endpoints.projects)
      .then((res) => {
        if (!res.ok) {
          throw new Error('Network response was not ok');
        }
        return res.json();
      })
      .then((res) => {
        console.log('Fetched data:', res); // Log fetched data
        if (Array.isArray(res.projects)) {
          setData(res.projects);
        } else {
          throw new Error('Data fetched is not an array');
        }
      })
      .catch((err) => console.error('Fetch error:', err));
  }, []);

  const numberOfItems = showMore && Array.isArray(data) ? data.length : 6;

  if (!data) {
    return <FallbackSpinner />;
  }

  return (
    <StyledContainer theme={theme}>
      <Fade>
        <Row>
          <Col>
            <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>
              {header}
            </h2>
          </Col>
        </Row>
        <Row>
          {Array.isArray(data) && data.slice(0, numberOfItems).map((project) => (
            <Col  sm={12} md={6} lg={4} className="mb-4">
              <StyledCard theme={theme}>
                {project.image && (
                  <StyledCardImage
                    variant="top"
                    src={project.image}
                    alt={project.title}
                  />
                )}
                <Card.Body>
                  <Card.Title>{project.title}</Card.Title>
                  <Card.Text>{project.bodyText}</Card.Text>
                  {project.links && project.links.map((link, linkIndex) => (
                    <Button
                      // key={linkIndex}
                      variant="link"
                      href={link.href}
                      target="_blank"
                      rel="noopener noreferrer"
                      style={{ display: 'block' }}
                    >
                      {link.text}
                    </Button>
                  ))}
                </Card.Body>
              </StyledCard>
            </Col>
          ))}
        </Row>
        {data.length > 6 && (
          <Row className="justify-content-center">
            <StyledButton theme={theme} onClick={() => setShowMore(!showMore)}>
              {showMore ? 'Show Less' : 'Load More'}
            </StyledButton>
          </Row>
        )}
      </Fade>
    </StyledContainer>
  );
};

Projects.propTypes = {
  header: PropTypes.string.isRequired,
};

export default Projects;
